package raw.java.gui;

import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import raw.java.map.Map;

/**
 * Listener for the reset button.
 * @author andreas
 */
public class AL_ResetButton implements ActionListener{	
	private MapPanel mPanel;
	
	/**
	 * Constructor. Is passed a reference to a MapPanel.
	 * @param mp  the MapPanel the JButton will be linked to.
	 */
	public AL_ResetButton(MapPanel mp) {
		mPanel = mp;
	}

	/**
	 * Called by the JButton button. Resets the simulation with new seed and new size.
	 * @param arg0  an event generated by the button this listener is listening on.
	 */
	@Override
	public void actionPerformed(ActionEvent arg0) {
		// SIZE, SEED
		//mPanel.getMap().simulationStop();
		mPanel.getMap().simulationResetStop();
		mPanel.getMap().setMapSize(mPanel.getDefaultSize());
		mPanel.getMap().simulationReset();
		mPanel.repaint();
	}
}